(9, 'KEGIATAN', 'YUDISIUM  DESEMBER 2012', '<p>Yudisium Sarjana</p>\r\n<p>Fakultas Kesehatan Masyarakat</p>\r\n<p>Universitas Mulawarman</p>\r\n<p>Samarinda, 17 Desember 2012</p>', '2013-03-15 09:51:31'),
(10, 'KEGIATAN', 'KULIAH UMUM', '<p>Kuliah Umum Fakultas Kesehatan Masyarakat dan Pelantikan Pengurus IAKMI Kalimantan Timur Periode 2012-2016</p>\r\n<p>Tema&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; : \" Standarisasi dan Kompetensi Sarjana Kesehatan Masyarakat</p>\r\n<p>Hari/Tanggal &nbsp; : Rabu, 19 Desember 2012</p>\r\n<p>Speaker &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; 1. dr. Adang bachtiar, MPH,ScD</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; 2. Prof. Dr. Tri Martiana, MS</p>', '2013-03-12 18:45:11'),
(11, 'PENGUMUMAN', 'PENGUMPULAN JURNAL', '<p>Diberitahukan Kepada seluruh Calon Wisudawan/Wisudawati Fakultas Kesehatan Masyarakat Universitas Mulawarman Gelombang IV desember 2012 untuk mengumpulkan jurnal sebagai syarat pengambilan Transkip Nilai.</p>\r\n<p>&nbsp;</p>\r\n<p style=\"text-align: left;\">Akademik FKM-UNMUL</p>', '2012-12-20 09:10:29'),
(12, 'PENGUMUMAN', 'INFORMASI Semester genap Tahun Akademik 2012/2013', '<p style=\"text-align: justify;\">Dengan hormat, sehubungan telah memasuki <strong>semester genap tahun akademik 2012/2013</strong> dan sesuai dengan Kalender Akademik 2012/2013, kami informasikan beberapa hal sebagai berikut \"</p>\r\n<ol>\r\n<li style=\"text-align: justify;\">Pengumpulan DPNA dan pengolahan KHS/Input Nilai oleh Dosen/Operator Fakultas, pada tanggal <strong>7</strong> s/d 14 <strong>Januari 2013.</strong></li>\r\n<li style=\"text-align: justify;\">Penyampaian Rekapitulasi Kehadiran Dosen Mengajar ke BAAK, pada tanggal <strong>23</strong> s/d<strong> 28 Januari 2013.</strong></li>\r\n<li style=\"text-align: justify;\">Penawaran/Penyusunan Jadwal mata kuliah Semester Genap 2012/2013, pada tanggal <strong>2</strong> s/d 12 <strong>Januari 2013.</strong></li>\r\n<li style=\"text-align: justify;\">Pembayaran SPP dan Pendaftaran Ulang Mahasiswa, pada tanggal <strong>8</strong> <strong>Januari</strong> s/d <strong>4 Februari 2013</strong></li>\r\n</ol>\r\n<p style=\"text-align: justify;\">Demikian kami informasikan kiranya kegiatan tersebut dapat dilaksanakan sesuai dengan jadwal, atas perhatian dan kerja samanya disampaikan terima kasih.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: right;\">ttd</p>\r\n<p style=\"text-align: right;\">&nbsp;</p>\r\n<p style=\"text-align: right;\">Akademik FKM UNMUL</p>', '2013-01-09 15:25:42'),
