(99, 'Peminatan Angkatan 2016', 'PENGUMUMAN', 'Peminatan Angkatan 2016', '<p><img title=\"Peminatan\" src=\"../file/media/622173999d8491882ecffb25d8c25d5c.jpg\" alt=\"\" width=\"823\" height=\"1164\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;<img title=\"Peminatan\" src=\"../file/media/e61d4cdab3398ea73b6a6e4e159205cd.jpg\" alt=\"\" width=\"674\" height=\"1125\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;<img title=\"Peminatan\" src=\"../file/media/4ee0ed3e60759711f6cf7e0f46b68889.jpg\" alt=\"\" width=\"672\" height=\"1122\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img title=\"Peminatan\" src=\"../file/media/891bbae6d4d2446bc19210793462e5de.jpg\" alt=\"\" width=\"686\" height=\"1149\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;<img title=\"Peminatan\" src=\"../file/media/77ab58826662428a990fcee7a8c12743.jpg\" alt=\"\" width=\"678\" height=\"560\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', '2018-06-07 14:44:02', '', '', NULL),
