(134, 'Lecture Theatre', 'fasilitasXlecture-theatre', 4, 28, '<p><img alt=\"\" src=\"https://fkm.unmul.ac.id/writable/uploads/content/galeri/1725327929_8137e6eb811d845031e9.jpg\" style=\"height:80%; width:80%\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://fkm.unmul.ac.id/writable/uploads/content/galeri/1725327867_f31a562635847891e26f.jpg\" style=\"height:80%; width:80%\" /></p>\r\n', NULL, '201810199509181001', '2024-09-03'),
(135, 'Zona Integritas', 'zona-integrasiXzona-integritas', 1, 44, '<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><strong>Judul: E-Book Integrity At Your Fingertips: Zona Integritas FKM UNMUL</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\">Integritas adalah landasan yang fundamental bagi sebuah institusi pendidikan tinggi. Sebagai Fakultas Kesehatan Masyarakat Universitas Mulawarman, kami berkomitmen kuat untuk menegakkan nilai-nilai integritas di lingkungan akademik kami.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\">Melalui e-book ini, kami ingin berbagi dengan seluruh civitas academika FKM UNMUL tentang pentingnya zona integritas dan bagaimana kami menerapkannya. Di dalam buku ini, Anda akan menemukan penjelasan terkait zona integritas, integritas akademik, dan penerapan zona integritas di lingkungan Fakultas Kesehatan Masyarakat Universitas Mulawarman.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\">Zona Integritas FKM UNMUL dirancang sebagai sistem yang transparan, akuntabel, dan berpedoman pada peraturan perundang-undangan yang berlaku diantaranya adalah </span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\">Peraturan Menteri Pendayagunaan Aparatur Negara dan Reformasi Birokrasi Republik Indonesia Nomor 90 Tahun 2021 tentang Pembangunan dan Evaluasi Zona Integritas Menuju Wilayah Bebas dari Korupsi dan Wilayah Birokrasi Bersih dan Melayani di Instansi Pemerintah.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\">Keputusan Mendikbud RI Nomor 1176/P/2020 tentang Pedoman Pembangunan Zona Integritas menuju Wilayah Bebas dari Korupsi/Wilayah Birokrasi Bersih dan Melayani Kementerian Pendidikan dan Kebudayaan</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\">Peraturan Menteri Pendidikan, Kebudayaan, Riset, dan Teknologi Republik Indonesia Nomor 39 Tahun 2021 tentang Integritas Akademik dalam Menghasilkan Karya Ilmiah.</span></li>\r\n</ol>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\">. Dengan adanya sistem ini, kami berharap dapat menciptakan lingkungan akademik yang sehat, bebas dari praktik-praktik yang melanggar etika dan norma.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\">Kami mengajak seluruh mahasiswa, dosen, dan staf di lingkungan FKM UNMUL untuk memahami dan berpartisipasi aktif dalam menjaga integritas akademik. Bersama-sama, kita dapat memastikan kualitas pendidikan, penelitian, dan layanan kemasyarakatan yang prima, sehingga FKM UNMUL semakin dipercaya dan diakui keunggulannya.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\">Selamat membaca dan semoga e-book ini dapat menjadi panduan praktis bagi kita semua dalam mewujudkan zona integritas di FKM UNMUL.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\">E-Book diunduh di link <a href=\"https://fkm.unmul.ac.id/writable/uploads/content/dokumen/1730879651_c8a84baac08442cbc033.pdf\" target=\"_blank\">DOWNLOAD</a></span></p>\r\n\r\n<p><img alt=\"\" src=\"https://fkm.unmul.ac.id/writable/uploads/content/galeri/1730879628_e6f0ee5c5ad8efefb4dd.png\" style=\"height:60%; width:60%\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://fkm.unmul.ac.id/writable/uploads/content/galeri/1730879620_6fc86d11a533cdee6989.png\" style=\"height:60%; width:60%\" /></p>\r\n', NULL, '201810199509181001', '2025-03-05'),
(136, 'Pedoman Akademik', 'akademikXpedoman-akademik', 3, 35, '<p>Berikut adalah Pedoman Akademik Program Studi S1 Kesehatan Masyarakat Universitas Mulawarman</p>\r\n\r\n<p>SIlahkan Download <a href=\"https://fkm.unmul.ac.id/writable/uploads/content/dokumen/1727056956_a0e82d21fa30d43982c9.pdf\">Disini</a>&nbsp;<br />\r\n<br />\r\nBerikut adalah Pedoman Akademik Program Studi S2 Kesehatan masyarakata Universitas Mulawarman<br />\r\nSilkahkan Download <a href=\"https://docs.google.com/document/d/1QzxXna2NgikDv0NDKIA_OflIdSRucWMY/edit\">Disini</a></p>\r\n', NULL, '201810199712191022', '2025-03-20'),
