(83, 'E - Learning', 'e-learning', 8, 28, '<p><strong>E-Learning FKM Unmul&nbsp;</strong></p>\r\n\r\n<p>E-Learning FKM Unmul adalah fasilitas media pembelajaran berbasis web yang menyediakan sarana&nbsp;belajar-mengajar yang terkomputerisasi yang dapat diakses oleh Dosen dan Mahasiswa dimana saja dengan jaringan internet, adapun fasilitas yang diberikan dari E -Learning FKM Unmul meliputi:</p>\r\n\r\n<ul>\r\n	<li>Menyediakan bahan ajar kuliah</li>\r\n	<li>Quiz&nbsp;dan&nbsp;Ujian&nbsp; CBT</li>\r\n	<li>Pengumpulan Tugas</li>\r\n	<li>Dan lain-lain</li>\r\n</ul>\r\n\r\n<p><strong>Alamat E-Learning :</strong></p>\r\n\r\n<p><strong><a href=\"http://elearning.fkm.unmul.ac.id/\" target=\"_blank\" title=\"Local E-Learning\">Klik Menuju E-Learning</a></strong></p>\r\n\r\n<p><strong><img alt=\"\" src=\"https://fkm.unmul.ac.id/public/upload/galeri/fd5029e9c4ff925bb59beb5c22132f66.jpg\" style=\"height:365px; width:632px\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', NULL, 'ajisudjay', '2023-09-28'),
(84, 'Unit Kegiatan Mahasiswa (UKM)', 'unit-kegiatan-mahasiswa-ukm-', 1, 29, '<p><strong>Unit Kegiatan Mahasiswa (UKM) yang ada di Fakultas Kesehatan Masyarakat Terdiri dari :</strong></p>\r\n\r\n<ol>\r\n	<li>Dewan Perwakilan Mahasiswa (DPM) Tingkat Fakultas</li>\r\n	<li>Badan Eksekutif Mahasiswa (BEM) Tingkat Fakultas</li>\r\n	<li>UKM Tingkat Fakultas</li>\r\n</ol>\r\n\r\n<p><strong>Bidang Seni</strong></p>\r\n\r\n<ol>\r\n	<li>Borneo Preventia Nusantara (BPN)</li>\r\n	<li>Gita Harmoni Preventia (GHP)</li>\r\n</ol>\r\n\r\n<p><strong>Bidang Keilmuan</strong></p>\r\n\r\n<ol>\r\n	<li>Pesut Mahakam</li>\r\n	<li>Insan Cendekia Preventia (ICP)</li>\r\n	<li>Organisasi Mahasiswa Peduli Lingkungan (OSEAN)</li>\r\n</ol>\r\n\r\n<p><strong>Bidang Keagamaan</strong></p>\r\n\r\n<ol>\r\n	<li>Shafiyyah Keluarga Muslim Al-Kautsar (Skm Al-Kautsar)</li>\r\n</ol>\r\n\r\n<p><strong>Bidang Olahraga</strong></p>\r\n\r\n<ol>\r\n	<li>Public Health Sport (PHS)</li>\r\n</ol>\r\n\r\n<p><img alt=\"\" src=\"https://fkm.unmul.ac.id/public/upload/galeri/3cd2eaf0f6e67f4cfb1d9f534266fac3.JPG\" style=\"height:410px; width:686px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://fkm.unmul.ac.id/public/upload/galeri/9f2b23690c7a9bf69b50f0f1d0e713fe.JPG\" style=\"height:618px; width:685px\" /></p>\r\n', NULL, 'ajisudjay', '2023-09-28');
